select.if:
SELECT paw.id,paw.pawn_no number FROM object_user_relation our LEFT JOIN bt_pawn paw
ON paw.id=our.object_id
WHERE our.stateflag=0 AND our.accept_status=0 AND our.object_type=#{objectType}
AND our.user_id=#{userId}

    AND paw.id IN

        #{objectIds.id}

SELECT iou.id,iou.iou_no number FROM object_user_relation our LEFT JOIN bt_iou iou
ON iou.id=our.object_id
WHERE our.stateflag=0 AND our.accept_status=0 AND our.object_type=#{objectType}
AND our.user_id=#{userId}

    AND iou.id IN

        #{objectIds.id}

mapper.select:
SELECT bor.object_id FROM t_business_obj_re bor LEFT JOIN business b ON b.id=bor.business_id WHERE bor.object_type=11 AND bor.stateflag=0 AND b.`status` in (1,3) AND bor.object_id IN ( SELECT tct.object_id FROM t_company_team tct LEFT JOIN t_company_team_re tctr ON tctr.company_team_id=tct.id LEFT JOIN t_user_tag tut ON tut.user_id=tctr.accepter_id WHERE tctr.type IN (0,1,2) AND tut.user_type IN (31,32,33) AND tctr.`status`=1 AND tct.object_type=#{objectType}
AND tctr.accepter_id=#{userId}
UNION SELECT tut.object_id from t_user_team tut LEFT JOIN t_teammate_re ttr ON ttr.user_team_id=tut.id WHERE tut.stateflag=0 AND tut.`status`=0 AND ttr.stateflag=0 AND ttr.`status`=1
AND ttr.user_id=#{userId}
)
