mapper.delete:
UPDATE t_nav_unview_userinfo SET stateflag=now(),oper_user=#{userId} WHERE stateflag=0

    AND object_type=#{object}


    AND object_id=#{objectId}

AND nav_id=#{navId}

mapper.select:
SELECT nuu.id,nuu.user_id reId,CONCAT(IF(tut.user_type=1,'平台管理员', IF(tut.user_type=2,'委托号', IF(tut.user_type=31,'处置号-催收', IF(tut.user_type=32,'处置号-律所', IF(tut.user_type=33,'处置号-中介','普通个体'))))),'|',tui.account,'|',tui.real_name) showName from t_nav_unview_userinfo nuu LEFT JOIN t_user_info tui ON tui.id=nuu.user_id LEFT JOIN t_user_tag tut ON tut.user_id=tui.id WHERE nuu.stateflag=0
AND nuu.object_type=#{object}
AND nuu.object_id=#{objectId}
AND nuu.nav_id IN
#{id}
(0)

SELECT ttr.user_team_id userTeamId,ttr.type,ttr.join_type joinType FROM t_user_team tut LEFT JOIN t_teammate_re ttr
ON ttr.user_team_id=tut.id
WHERE ttr.stateflag=0 AND ttr.status=1 AND ttr.user_id=#{userId}
    AND tut.object_type=#{object}
    AND tut.object_id=#{objectId}
