SELECT tusi.real_name,bor.name,ttr.user_id,ttr.type,tusi.avg,ttr.status,ttr.join_type from t_teammate_re ttr LEFT JOIN t_user_tag tust ON tust.user_id=ttr.user_id LEFT JOIN t_user_team tut ON tut.id=ttr.user_team_id LEFT JOIN bt_organization bor ON bor.id=tust.team_id LEFT JOIN t_user_info tusi ON tusi.id=ttr.user_id WHERE tut.object_id=#{objectId,jdbcType=INTEGER} and tut.object_type=#{objectType,jdbcType=INTEGER} and tut.company_id=#{companyId,jdbcType=INTEGER} and ttr.status IN
#{status}
and tut.stateflag=0 and tust.stateflag=0

SELECT count(tut.id) finish,count(tut2.id) total
from t_teammate_re ttr LEFT JOIN t_user_team tut
ON tut.id=ttr.user_team_id

    and tut.object_type=#{objectType,jdbcType=INTEGER}

and tut.status=99 and tut.company_id=#{companyId,jdbcType=INTEGER}
LEFT JOIN t_user_team tut2 ON tut2.id=ttr.user_team_id

    and tut2.object_type=#{objectType,jdbcType=INTEGER}

and tut2.company_id=#{companyId,jdbcType=INTEGER}
where ttr.stateflag=0 AND ttr.`status`=1 and ttr.user_id=#{userId,jdbcType=INTEGER}

SELECT tci.company_name companyName,tui.company_id companyId,tui.real_name realName,tui.user_name userName,tui.id userId,tut.role_id roleType,tut.user_type userType from t_company_info tci LEFT JOIN t_user_info tui ON tui.company_id=tci.id LEFT JOIN t_user_tag tut ON tut.user_id=tui.id WHERE tui.stateflag=0 AND tci.stateflag=0 AND tci.id in (
#{companyId,jdbcType=INTEGER}
SELECT tui2.company_id from t_user_info tui2 where tui2.id=#{userId,jdbcType=INTEGER}
)
AND (tui.real_name LIKE CONCAT('%',#{realName,jdbcType=VARCHAR},'%') OR tui.user_name LIKE CONCAT('%',#{realName,jdbcType=VARCHAR},'%'))

