mapper.select:
SELECT tze.id estatesId,tze.object_id pawnId,tze.orientation,CONCAT(tze.house_s,'-',tze.house_t,'-',tze.house_c,'-',house_w) houseType,tze.house_no houseNo, tze.selling_price priceTotal,tze.guide_price priceUnit,tzk.id keyId,tze.acreage ,DATE_FORMAT(tzo.entrust_protocol_time,'%Y-%m-%d') entrustTime, tzea.plot_name title,CONCAT(tzea.floor,' (',tze.replenish,'/',tzea.floor_total,')') floor, CONCAT((SELECT name FROM t_area WHERE id=tzea.province),(SELECT name FROM t_area WHERE id=tzea.city),(SELECT name FROM t_area WHERE id=tzea.area), CONCAT(tzea.road,'路'),tzea.plot_name,CONCAT(tzea.building_no,'栋'),CONCAT(tzea.element,'单元'),CONCAT(tzea.floor,'层')) place from t_zcy_estates tze LEFT JOIN t_zcy_owner tzo ON tzo.estates_id=tze.id LEFT JOIN t_zcy_maintain tzm ON tzm.estates_id=tze.id LEFT JOIN t_zcy_key tzk ON tzk.estates_id=tze.id LEFT JOIN t_zcy_express tzex ON tzex.estates_id=tze.id LEFT JOIN t_zcy_estates_address tzea ON tzea.estates_id=tze.id
tze.stateflag=0
LIMIT #{zcyListQuery.startPage},#{zcyListQuery.pageCount}

