mapper.select:
select

from t_teammate_re

    stateflag=0

        AND user_team_id=#{userTeamId,jdbcType=INTEGER}


        AND type=#{type,jdbcType=INTEGER}


        AND user_id=#{userId,jdbcType=INTEGER}

SELECT object_id FROM t_user_team WHERE object_type = #{type,jdbcType=INTEGER} AND id IN (
  SELECT user_team_id FROM t_teammate_re where user_id = #{userId,jdbcType=INTEGER}
  AND join_type = #{joinType,jdbcType=INTEGER} AND stateflag = 0
)AND stateflag = 0

SELECT  ore.*  FROM   object_user_relation ore ,t_teammate_re tre
where ore.object_type =#{objectType,jdbcType=INTEGER}
and ore.object_id =#{objectId,jdbcType=INTEGER}
and tre.user_team_id=ore.employer_id
and ore.type =1  and tre.user_id =#{userId,jdbcType=INTEGER}

mapper.update:
UPDATE t_teammate_re ttr set ttr.status=99,ttr.stateflag=now()
WHERE ttr.user_team_id IN

    #{id}
