mapper.select:
SELECT ttr.id from t_user_team tut LEFT JOIN t_teammate_re ttr
ON ttr.user_team_id=tut.id
WHERE tut.object_id=#{objectId,jdbcType=INTEGER} and tut.object_type=#{objectType,jdbcType=INTEGER} and ttr.user_id=#{userId,jdbcType=INTEGER}

SELECT m.id,m.content,m.create_at,m.liquidate_stage,u.user_name,u.sex,c.company_name FROM t_follow_up_message m LEFT JOIN t_user_info u ON m.user_id = u.id LEFT JOIN t_company_info c on u.company_id =c.id WHERE 1=1
AND m.liquidate_stage = #{liquidateStage,jdbcType=INTEGER}
AND m.second_liquidate_stage = #{secondLiquidateStage,jdbcType=INTEGER}
ORDER BY u.create_at
