SELECT tap.* FROM t_business_obj_re bor LEFT JOIN business b
ON b.id=bor.business_id RIGHT JOIN ( SELECT

FROM t_news WHERE 1=1

) tap
ON tap.id=bor.object_id
WHERE bor.object_type=#{objectType}

    AND b.status=#{businessStatus}


    ORDER BY open_time desc


    ORDER BY open_time ASC

Limit #{startPage},#{pageCount}

